import React from "react";
import Squircle from "./components/Squircle";
import { Music4 } from "lucide-react";

const Squircles = () => {
  return (
    <div className="w-full flex justify-center items-center">
      <Squircle>
        <div className="aspect-square w-[200px] flex justify-center items-center bg-linear-to-t from-[#FA233B] to-[#FB5C74]">
          <Music4 className="w-[100px] h-[100px] text-white" />
        </div>
      </Squircle>
    </div>
  );
};

export default Squircles;

'use client';

import { useEffect } from 'react';


export function useSquircle() {
  useEffect(() => {
    if (typeof window !== "undefined" && "paintWorklet" in CSS) {
      // TypeScript-safe cast
      (CSS as unknown as { paintWorklet: Worklet }).paintWorklet.addModule(
        "https://unpkg.com/css-houdini-squircle@0.3.0/lib/squircle.js"
      );
    }
  }, []);
}

// components/Squircle.tsx
"use client";

import { useSquircle } from "../hooks/useSquircle";

export default function Squircle({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  useSquircle();

  const squircleStyle: React.CSSProperties = {
    maskImage: "paint(squircle)",
    WebkitMaskImage: "paint(squircle)",
    borderRadius: "40px", // required for squircle to show properly
    // Use proper type for CSS properties (avoid 'any')
    "--squircle-radius": "40px",
    "--squircle-smooth": "1",
    "--squircle-outline": "none",
  } as React.CSSProperties;

  return (
    <div className="w-auto h-auto" style={squircleStyle}>
      {children}
    </div>
  );
}
